SELECT t5.video_id, t5.goods_id, t5.like_num,t5.usermd5, t5.shop_name, t5.address, t5.dividend, t5.videomd5,t5.videoimglist, IFNULL((tvur.user_id),0)AS isUpvote FROM ( SELECT t4.u_id AS video_id, t4.goods_id, t4.like_num, t4.usermd5, t4.shop_name, t4.address, t4.dividend, t4.videomd5, t4.videoimglist FROM (SELECT tv.u_id AS videoid ,tgc.class_name FROM t_video_goods tvg , t_goods_info_spec tgis, t_video tv,t_goods_info tgi, t_goods_class tgc WHERE tgis.u_id=tvg.goodsspec_id AND tv.u_id=tvg.video_id AND tgi.u_id=tgis.goods_id AND tgi.class_code=tgc.class_code AND tv.`status`=3
AND tgis.size=#{size}
AND tgc.class_code=#{class_code}
AND tgis.color=#{color}
) t3 LEFT JOIN (SELECT t1.u_id, t1.goods_id, IFNULL(t1.like_num, 0) AS like_num, t1.usermd5, t1.shop_name, t1.address, IFNULL( t1.dividend, 0) AS dividend, t1.videomd5, t2.videoimglist FROM
(SELECT tv.u_id, tvu.goods_id , tv.like_num, sr.path AS userImgUrl, sr.md5 AS usermd5, ts.shop_name, ts.address, tgb.dividend, srv.path AS videopath, srv.md5 AS videomd5 FROM t_video tv LEFT JOIN t_video_user tvu ON tvu.video_id= tv.u_id LEFT JOIN t_user_info tui ON tvu.user_id= tui.user_id LEFT JOIN t_metadata_rel tmr ON tmr.record_id=tui.avatar_id LEFT JOIN s_resources sr ON tmr.data_id= sr.u_id LEFT JOIN t_metadata_rel tmrv ON tmrv.record_id=tv.video_url AND tmrv.type = 1 LEFT JOIN s_resources srv ON tmrv.data_id= srv.u_id LEFT JOIN t_shop ts ON ts.u_id = tvu.shop_id LEFT JOIN t_goods_percentage tgp ON tgp.goods_id = tvu.goods_id LEFT JOIN t_goods_brokerage tgb ON tgp.u_id= tgb.percentage_id) t1 LEFT JOIN ( SELECT GROUP_CONCAT(t.md5) AS videoimglist, t.u_id FROM ( SELECT sr.md5, tv.u_id FROM t_video tv LEFT JOIN t_metadata_rel tmr ON tv.video_url=tmr.record_id LEFT JOIN s_resources sr ON tmr.data_id=sr.u_id AND tmr.data_type=4 ) t GROUP BY t.u_id) t2
ON t1.u_id=t2.u_id) t4 ON t3.videoid=t4.u_id) t5 LEFT JOIN t_video_upvote_record tvur ON tvur.video_id=t5.video_id AND tvur.user_id=#{userId} LIMIT #{pageIndex}, #{pageSize}

SELECT COUNT(tv.u_id) AS videoid  ,tgc.class_name  FROM t_video_goods tvg , t_goods_info_spec tgis, t_video tv,t_goods_info tgi, t_goods_class tgc
WHERE   tgis.u_id=tvg.goodsspec_id AND tv.u_id=tvg.video_id AND tgi.u_id=tgis.goods_id AND tgi.class_code=tgc.class_code AND tv.`status`=3


    AND tgis.size=#{size}


    AND tgc.class_code=#{class_code}


    AND tgis.color=#{color}
